<template>
    <div class="course-detail" v-if="course && currentGroup">
        <div class="container">
            <div class="breadcrumbs">
                <router-link to="/courses">
                    <svg width="13" height="22" viewBox="0 0 13 22" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path d="M12 1L2 11L12 21" stroke="#2E79B9" stroke-width="2"/>
                    </svg>
                    <div class="breadcrumbs__title">{{ currentGroup.name }}</div>
                </router-link>
            </div>
            <div class="course-detail__row">
                <div class="course-detail__left">
                    <div class="course-detail__title">
                        {{ course.name }}
                    </div>
                    <div class="course-detail__left-img" v-if="course">
                        <img :src="`${BaseUrl}/image/${course.images[course.images.length - 1]}`" alt="">
                    </div>
                    <div class="course-detail__description" data-aos="fade-left" data-aos-duration="1000">
                        <div class="course-detail__description-label">
                            <div class="course-detail__description-icon">
                                <svg width="45" height="45" viewBox="0 0 45 45" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                                    <rect width="45" height="45" fill="url(#pattern0_1004_324)"/>
                                    <defs>
                                        <pattern id="pattern0_1004_324" patternContentUnits="objectBoundingBox" width="1" height="1">
                                            <use xlink:href="#image0_1004_324" transform="translate(-0.225806 -0.225806) scale(0.00283518)"/>
                                        </pattern>
                                        <image id="image0_1004_324" width="512" height="512" preserveAspectRatio="none" xlink:href="data:image/png;base64,"/>
                                    </defs>
                                </svg>
                            </div>
                            COURSE DESCRIPTION:
                        </div>
                        <div class="course-detail__description-content" v-html="course.description" />
                    </div>
                    <div class="course-detail-achive" v-if="course.achievements" data-aos="fade-left" data-aos-duration="1600">
                        <div class="course-detail__description">
                            <div class="course-detail__description-label">
                                <div class="course-detail__description-icon">
                                    <svg width="45" height="45" viewBox="0 0 45 45" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                                        <rect width="45" height="45" fill="url(#pattern0_1004_321)"/>
                                        <defs>
                                            <pattern id="pattern0_1004_321" patternContentUnits="objectBoundingBox" width="1" height="1">
                                                <use xlink:href="#image0_1004_321" transform="scale(0.00195312)"/>
                                            </pattern>
                                            <image id="image0_1004_321" width="512" height="512" preserveAspectRatio="none" xlink:href="data:image/png;base64,"/>
                                        </defs>
                                    </svg>
                                </div>
                                WHAT YOU WILL ACHIEVE?
                            </div>
                            <div class="course-detail__description-content" v-html="course.achievements" />
                        </div>
                    </div>
                    <div class="course-detail-goals" v-if="course.nutrition" data-aos="fade-left" data-aos-duration="2000">
                        <div class="course-detail__description">
                            <div class="course-detail__description-label">
                                <div class="course-detail__description-icon">
                                    <svg width="45" height="45" viewBox="0 0 45 45" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                                        <rect width="45" height="45" fill="url(#pattern0_1004_328)"/>
                                        <defs>
                                            <pattern id="pattern0_1004_328" patternContentUnits="objectBoundingBox" width="1" height="1">
                                                <use xlink:href="#image0_1004_328" transform="scale(0.00195312)"/>
                                            </pattern>
                                            <image id="image0_1004_328" width="512" height="512" preserveAspectRatio="none" xlink:href="data:image/png;base64,"/>
                                        </defs>
                                    </svg>
                                </div>
                                NUTRITION GOALS
                            </div>
                            <div class="course-detail__description-content" v-html="course.nutrition"/>
                        </div>
                    </div>
                    <div class="course-detail__bottom course-detail__bottom--desk">
                        <div class="course-detail__price">
                            <span class="course-detail__price-label">
                                price:
                            </span>
                            <span class="course-detail__price-value">
                                {{ course.prices.find((el) => el.currency === currentCurrency).amount }} {{ currentValue }}
                            </span>
                        </div>
                        <div class="course-detail__add-to-cart course-detail__add-to-cart--desktop primary-button-hover" @click="addToCart">
                            Add to cart
                        </div>
                    </div>
                </div>
                <div class="course-detail__right">
                    <div class="course-detail__right-img" v-if="course">
                        <img :src="`${BaseUrl}/image/${course.images[course.images.length - 1]}`" alt="">
                    </div>
                    <div class="course-detail__right-content">
                        <div class="course-detail__content-label">
                            Table of Contents:
                        </div>
                        <div class="course-detail__content" v-html="course.content" />
                    </div>
                </div>
                <div class="course-detail__bottom course-detail__bottom--mob">
                    <div class="course-detail__price">
                            <span class="course-detail__price-label">
                                price:
                            </span>
                        <span class="course-detail__price-value">
                                {{ course.prices.find((el) => el.currency === currentCurrency).amount }} {{ currentValue }}
                            </span>
                    </div>
                    <div class="course-detail__add-to-cart course-detail__add-to-cart--mobile primary-button-hover" @click="addToCart">
                        Add to cart
                    </div>
                </div>
            </div>
        </div>
    </div>
</template>
<script setup>
import ArrowRight from "@/img/svg/arrow-right.svg";
import {useRoute, useRouter} from "vue-router";
import api from "@/services/client.js";
import {computed, onMounted, ref} from "vue";
import {notify} from "@kyvg/vue3-notification";
import {useMainStore} from "@/stores/main.js";
const router = useRouter();
const route = useRoute();
const course = ref();
const groups = ref();
const mainStore = useMainStore();
const currentCurrency = computed(() => mainStore.currency);
const currentGroup = computed(() => groups.value && groups.value.find((el) => el.id === course.value?.group))
const currentValue = computed(() => currentCurrency.value === 'EUR' ? '€': '£');
onMounted(() => {
    fetchGroups();
    fetchPreviewCourse(route.params.id)
});
const BaseUrl = ref(import.meta.env.VITE_BASE_API);
function setCourses(data) {
    groups.value = data;
}
async function fetchGroups() {
    const response = await api.get('/groups');
    setCourses(response);
}
const fetchPreviewCourse = async (id) => {
    course.value = await api.get(`/courses/${id}`);
}
const addToCart = async () => {
    if (!mainStore.isAuth) {
        router.push({name: 'sign-in'})
        return;
    }
    try {
        await api.put('/add-to-basket', {}, {
            params: {
                pid: course.value.id,
            }
        })
        await router.push('/courses')
        notify({
            type: 'success',
            text: 'Product successfully added to cart',
        })
    } catch (e) {
        notify({
            type: 'error',
            text: e?.response?.data?.detail,
        })
    }
}
</script>
<style lang="scss">
.course-detail {
    .breadcrumbs {
        background: #1969AD;
        font-style: italic;
        font-family: "AtkinsonHyperlegible", serif;
        font-weight: 700;
        background: linear-gradient(to right, #1969AD 3%, #7EB8E8 100%);
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
    }
    margin-top: 20px;
    @include mqm(1024) {
        margin-top: 30px;
    }
    &__bottom {
        display: flex;
        width: 100%;
        gap: 20px;
        align-items: center;
        margin-top: 50px;
        margin-bottom: 70px;
        @include mqm(1024) {
            gap: 50px;
        }
    }
    &__bottom--desk {
        display: none;
        @include mqm(1024) {
            display: flex;
        }
    }
    &__bottom--mob {
        display: flex;
        @include mqm(1024) {
            display: none;
        }
    }
    &__price {
        flex: 17%;
        @include mqm(1024) {
            flex: unset;
        }
    }
    &__price-label {
        font-family: 'Inter', sans-serif;
        font-style: italic;
        font-size: 14px;
        color: #000;
        display: block;
    }
    &__price-value {
        font-family: 'Inter', sans-serif;
        font-weight: 700;
        font-style: italic;
        font-size: 24px;
        color: #1969AD;
    }
    &__left {
        order: 2;
        @include mqm(1024) {
            flex: 65%;
        }
    }
    &__title {
        margin-top: 10px;
        font-size: 24px;
        line-height: 25px;
        font-weight: 700;
        font-family: 'Inter', sans-serif;
        @include mqm(1024) {
            line-height: 50px;
            font-size: 50px;
        }
    }
    &__description-label {
        font-family: 'Inter', sans-serif;
        display: flex;
        align-items: center;
        gap: 15px;
        font-weight: 500;
        margin-top: 40px;
        color: #0F4A7C;
        @include mqm(1024) {
            font-size: 24px;
        }
    }
    &__content {
        font-family: 'Inter', sans-serif;
        font-size: 14px;
    }
    &__row {
        @include mqm(1024) {
            display: flex;
            gap: 120px;
        }
    }
    &__right {
        margin-top: 30px;
        @include mqm(1024) {
            flex: 35%;
        }
    }
    &__right-content {
        padding: 1px 20px 30px 20px;
        border-radius: 30px;
        margin-top: 30px;
        background: linear-gradient(90deg, rgba(26,105,173,1) 0%, rgba(11,43,71,1) 100%);
        @include mqm(1024) {
            border: 1px solid rgba(0, 0, 0, 0.3);
            padding: 20px;
        }
    }
    &__left-img {
        display: block;
        margin-top: 30px;
        img {
            width: 100%;
            height: 100%;
            border-radius: 30px;
        }
        @include mqm(1024) {
            margin-top: 0;
            display: none;
        }
    }
    &__right-img {
        display: none;
        img {
            width: 100%;
            height: 100%;
            border-radius: 30px;
        }
        @include mqm(1024) {
            display: block;
        }
    }
    &__add-to-cart {
        width: 100%;
        &.button {
            &--secondary {
                cursor: pointer;
                font-size: 20px;
                border-radius: 10px;
                justify-content: center;
                align-items: center;
                height: 60px;
                background-color: #576C7E;
                color: #fff;
                width: 100%;
                max-width: 413px;
            }
        }
        &--desktop {
            display: none;
            max-width: 413px;
            text-align: center;
            @include mqm(1024) {
                display: flex;
                justify-content: center;
                font-family: 'Inter', sans-serif;
                font-weight: 500;
                font-size: 20px;
                padding: 14px 0;
            }
        }
        &--mobile {
            display: flex;
            width: 100%;
            max-width: 100% !important;
            justify-content: center;
            font-family: 'Inter', sans-serif;
            font-weight: 500;
            font-size: 20px;
            padding: 14px 0;
            @include mqm(1024) {
                display: none;
            }
        }
    }
    &__content-label {
        margin: 30px 0;
        font-size: 24px;
        color: #fff;
        font-weight: 500;
        font-family: 'Inter', sans-serif;
        @include mqm(1024) {
            margin-top: 0;
        }
    }
    &__content {
        color: #fff;
    }
    &__description-content {
        margin-top: 20px;
        font-size: 14px;
        font-family: 'Inter', sans-serif;
        position: relative;
        padding-bottom: 30px;
        &::before {
            position: absolute;
            content: '';
            display: block;
            bottom: 0;
            border-bottom: 1px solid rgba(0, 0, 0, 0.3);
            width: 100%;
            max-width: 80%;
            left: 50%;
            transform: translate(-50%, 0);
        }
        @include mqm(1024) {
            font-size: 16px;
        }
    }
    .container {
        max-width: 1440px;
        margin: 0 auto;
        padding: 0 20px;
        @include mqm(1024) {
            padding: 0 80px;
        }
    }
    .course-detail-goals, .course-detail-achive {
        .course-detail__description-label {
            margin-top: 20px;
        }
    }
    .course-detail-goals {
        .course-detail__description-content {
            padding-bottom: 0;
            &::before {
                display: none;
                @include mqm(1024) {
                    display: block;
                }
            }
            @include mqm(1024) {
                padding-bottom: 30px;
            }
        }
    }
}

</style>
